package com.viraj.Comparisons;

public class Comparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean isYoung = false;
		System.out.println(isYoung);
		
		int age = 25;
		System.out.println(age);
		
		isYoung = age < 50;
		System.out.println(isYoung);
		
		System.out.println("\n--------------- NEW DATA --------------\n");
		
		boolean isPowerful = false;
		System.out.println("The value of isPowerful variable is: " + isPowerful);
		
		int energy = 57;
		System.out.println("The value of the energy is: " + energy);
		
		isPowerful = energy > 80;
		System.out.println("The value of isPowerful evaluates to: " + isPowerful);
		
		
		

	}

}
